import React, {useState, useContext} from 'react';
// import { useParams, useHistory } from 'react-router';
import { callApi } from '../CallApi';
import { UserContext } from '../UserContext';
// import { UserProvider } from '../UserContext';

const { REACT_APP_BASE_URL } = process.env;

const Register = () => {
    const {setToken} = useContext(UserContext);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // const params = useParams();
    // const history = useHistory();
    // console.log('params: ', params);
    const userRegister = async () => {
        try {
            const response = await callApi({
                url: "/users/register",
                method: "POST",
                body: {
                    username,
                    password
                }
            });
            console.log(response)
            if (response) {
                setToken(response.data.token)
                if (response.data.token) {
                    setToken("");
                }
            }
        }
        catch (error) {
            console.log(error);
        }
    }

    const usernameHandler = (event) => {
        setUsername(event.target.value);
    }

    const passwordHandler = (event) => {
        setPassword(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault(); //stops page from refreshing

        userRegister(); //function goes off to send information back to the api

        setUsername("") //onSubmit sets username to an empty string
        setPassword("") //onSubmit also sets the password to an empty string
    }

    return<>
        <form onSubmit={async (event) => {
            event.preventDefault();
            // const fetchUrl = `${REACT_APP_BASE_URL}/users/${params.method}`

        // const respObj = await resp.json();
        // if(respObj.data) {
        //     setToken(respObj.data.token);
        //     console.log(respObj.data)
        //     if (respObj.data.token) {
        //       history.push('/Home');
        //     }
        // }

        }}>
            <input type="text" placeholder="username" value={username}
            onChange={(event)=> setUsername(event.target.value)}></input>
            <hr></hr>
            <input type="password" placeholder="password" value={password}
            onChange={(event)=> setPassword(event.target.value)}></input>
            <hr></hr>

            <button type="submit">Submit</button>

        </form>
    </>
}

export default Register;
